hudson.tasks.Shell.command:
#!/bin/bash source /var/lib/jenkins/JobScripts/git_functions.sh source /var/lib/jenkins/JobScripts/jenkins_functions.sh source /var/lib/jenkins/JobScripts/util_functions.sh source /var/lib/jenkins/JobScripts/docker_functions.sh source /var/lib/jenkins/JobScripts/create_container_html.sh source /var/lib/jenkins/JobScripts/jenkins.properties source /var/lib/jenkins/JobScripts/git_functions_new.sh
SERVER_NAME=${BRANCH_NAME}_Server
echo "creating job config from template.." createJobConfigFromTemplate ~/JobScripts/WrapperTemplate.xml ${BRANCH_NAME} /tmp/config1.xml createJobConfigFromTemplate ~/JobScripts/UnitTestTemplate.xml ${BRANCH_NAME} /tmp/config2.xml createJobConfigFromTemplate ~/JobScripts/FunctionalTestTemplate.xml ${BRANCH_NAME} /tmp/config3.xml createJobConfigFromTemplate ~/JobScripts/StaticCodeAnalysisTemplate.xml ${BRANCH_NAME} /tmp/config4.xml
echo "${SERVER_NAME} ${BRANCH_NAME} instance_id server_ip 4940 5901 8292" >> ${JENKINS_HOME}/portlookup/server_mapping
echo "create docker container" create_container "${BRANCH_NAME}" 4940 22 5901 5901 8292 80 vnc_php_test
sleep 1m
echo "Copying the database to the container.." setup_mysql "localhost" "4940" "${JENKINS_HOME}"
echo "copying the files to the container.." copy_files "localhost" "4940" "${JENKINS_HOME}"
echo "create image from container.." create_image_from_container ${BRANCH_NAME}
sleep 2m
echo "creating image dump.." create_image_dump ${BRANCH_NAME} ${JENKINS_HOME}
echo "image dump copy to s3" copyImageToS3 ${BRANCH_NAME} ${JENKINS_HOME}
echo "delete container" delete_container ${BRANCH_NAME}
echo "delete image" delete_image ${BRANCH_NAME}
echo "delete image dump" delete_image_dump ${BRANCH_NAME} ${JENKINS_HOME}
createJob "${BRANCH_NAME}MultiJob" /tmp/config1.xml createJob "${BRANCH_NAME}UnitTest" /tmp/config2.xml createJob "${BRANCH_NAME}FunctionalTest" /tmp/config3.xml createJob "${BRANCH_NAME}StaticCodeAnalysis" /tmp/config4.xml
echo "Created a docker container for feature branch ${BRANCH_NAME} with ports:" echo "SSH => 4940" echo "VNC => 5901" echo "NGINX => 8292"
BRANCH_AUTHOR_EMAIL=$(get_branch_author_email ${BRANCH_NAME}) echo "I've to send mail to $BRANCH_AUTHOR_EMAIL about the branch getting tracked by CI system" echo "EMAIL=$BRANCH_AUTHOR_EMAIL" > email.txt
