hudson.tasks.Shell.command:
#!/bin/bash
source /var/lib/jenkins/JobScripts/util_functions.sh
source /var/lib/jenkins/JobScripts/create_container_html.sh
source /var/lib/jenkins/JobScripts/git_functions.sh
source /var/lib/jenkins/JobScripts/docker_functions.sh
source /var/lib/jenkins/JobScripts/jenkins.properties
source /var/lib/jenkins/JobScripts/file_functions.sh
source /var/lib/jenkins/JobScripts/manage_instance.sh
source /var/lib/jenkins/JobScripts/service_functions.sh

HOST_SSH_PORT=4940

BRANCH_NAME={feature_branch}

SERVER_NAME={feature_branch}

echo "Launching Instance If Not Exist .."

instance_id=`createServerIfNotExist ${BRANCH_NAME} "${JENKINS_HOME}/portlookup/server_mapping" ${SERVER_NAME} ${IMAGE} ${SIZE} ${ENDPOINT} ${TOKEN} ${REGION} ${SSH_ID}`

server_ip=`python ~/JobScripts/manage_instance.py 'get_server_ip("'"$ENDPOINT"'","'"$TOKEN"'","'"$instance_id"'")'`

lineNumber=`getLineNoMatchingRegex ${JENKINS_HOME}/portlookup/server_mapping ${BRANCH_NAME}`

updateColumn ${JENKINS_HOME}/portlookup/server_mapping ${lineNumber} 3 ${instance_id}

updateColumn ${JENKINS_HOME}/portlookup/server_mapping ${lineNumber} 4 ${server_ip}


echo "Installing Docker.."
install_docker ${server_ip}

echo "pull Docker Image.."
pullImagefroms3 {feature_branch} ${server_ip}

sleep 1m

echo "Creating container.."
create_container_at_server "${BRANCH_NAME}" 4940 22 5901 5901 8292 80 ${server_ip}

ssh root@${server_ip} -p ${HOST_SSH_PORT} "rm -rf /usr/share/nginx/www/ProgrammableWeb"

echo "Copying code.";
copy_git_project ${server_ip} ${HOST_SSH_PORT} ${WORKSPACE}

echo "Linking to files and settings already present on the container.";

ssh root@${server_ip} -p ${HOST_SSH_PORT} "ln -s /var/data/files /usr/share/nginx/www/ProgrammableWeb/sites/default/files";
ssh root@${server_ip} -p ${HOST_SSH_PORT} "chown -R www-data /usr/share/nginx/www/ProgrammableWeb/sites/default/files";
ssh root@${server_ip} -p ${HOST_SSH_PORT} "ln -s /var/data/settings.php /usr/share/nginx/www/ProgrammableWeb/sites/default/settings.php";

change_settings ${server_ip} ${HOST_SSH_PORT} 

copy_setting_files ${server_ip} ${HOST_SSH_PORT} ${JENKINS_HOME}

ssh root@${server_ip} -p ${HOST_SSH_PORT} "service php5-fpm restart"
serviceNginxRestart ${server_ip} ${HOST_SSH_PORT}

ssh root@${server_ip} -p ${HOST_SSH_PORT} "cd /usr/share/nginx/www/ProgrammableWeb/sites && drush cc all"

ssh root@${server_ip} -p ${HOST_SSH_PORT} "cd /usr/share/nginx/www/ProgrammableWeb/tests && rm -rf vendor"

ssh root@${server_ip} -p ${HOST_SSH_PORT} "cd /usr/share/nginx/www/ProgrammableWeb/tests && php composer.phar self-update"

ssh root@${server_ip} -p ${HOST_SSH_PORT} "cd /usr/share/nginx/www/ProgrammableWeb/tests && php composer.phar update"

get_committers_email {feature_branch} ${BUILD_ID}

rm -rf /var/lib/jenkins/jobs/{feature_branch}MultiJob/env.properties

echo "server_ip=${server_ip}"  >> /var/lib/jenkins/jobs/{feature_branch}MultiJob/env.properties
echo "BRANCH_NAME=${BRANCH_NAME}" >> /var/lib/jenkins/jobs/{feature_branch}MultiJob/env.properties
